/** @jsxImportSource @emotion/react */
import { css } from '@emotion/react';

export const heroContainer = css({
    display: 'flex',
    maxWidth: 1200,
    margin: 'auto',
    padding: '0 100px',

    h1: {
        fontSize: '5em',
        // margin: '20px 0',
    }
})

export const heroSectionText = css({
    width: '50%',
    padding: '24px 40px',

    button: {
        backgroundColor: '#331B3B',
        padding: 20,
        color: '#FFF',
        fontWeight: 800,
        border: 'none',
        borderRadius: 12,
        cursor: 'pointer',

        '&:hover': {
            opacity: .7,
        }
    }
})

export const heroSectionImage = css({
    width: '50%',
    padding: 24,

    img: {
        maxWidth: '100%',
    },
})
