/** @jsxImportSource @emotion/react */
import { css } from '@emotion/react';

export const heroContainer = css({
    display: 'flex',
    maxWidth: 1200,
    margin: 'auto',

    h1: {
        fontSize: 24,
        margin: '20px 0',
    }
})

export const heroSectionText = css({
    width: '50%',
    padding: '40px 0 40px 20px',

    button: {
        backgroundColor: '#331B3B',
        padding: 20,
        color: '#FFF',
        fontWeight: 800,
        border: 'none',
        borderRadius: 12,
        cursor: 'pointer',

        '&:hover': {
            opacity: .7,
        }
    }
})

export const heroSectionImage = css({
    width: '50%',
    padding: 24,
    textAlign: 'right',

    img: {
        maxWidth: '90%',
    },
})
